var MongoClient = require( 'mongodb' ).MongoClient;

var _db;
var _collection;
var _client;

const credentials = {
  host: 'calculator.gg2rv.mongodb.net/myFirstDatabase',
  username: 'calculator',
  password: 'witcalculator',
  options: 'retryWrites=true&w=majority',
};

const url = "mongodb+srv://" 
              + credentials.username + ":" 
              + credentials.password + "@" + 
              credentials.host + "?"
              +credentials.options;

module.exports = {

  connectToServer: function( callback ) {
    
    MongoClient.connect( url,  { useNewUrlParser: true }, function( err, client ) {
      _db  = client.db('logger');
      /*_db.setProfilingLevel(uselogs); atlas free dont allow this command  */
      _collection = _db.collection('loggerCollection');
      _client = client;
      return callback( err );
    } );
  },

  getDb: function() {
    return _db;
  },

  getCollection: function() {
    return _collection;
  },

  getClient: function() {
    return _client;
  }
};